import React, { useEffect, useContext } from 'react';
import { Dimensions, SafeAreaView, StyleSheet, Text, View, FlatList, Image } from "react-native";
import { withNavigation } from "react-navigation";
import { Icon } from 'react-native-elements';
import { FontAwesome } from '@expo/vector-icons';
import { TextInput, TouchableOpacity } from 'react-native-gesture-handler';

import { COLORS } from '../consts/colors';
import { Context as BooksContext } from '../context/BooksContext'

import Card from '../components/Card';
const width = Dimensions.get('screen').width / 2 - 30;

const BooksListScreen = ({ navigation }) => {
  const categories = ['FAMILIE', 'SPIRITUALITATE', 'SANATATE', 'COPII'];
  const [categoryIndex, selectCategoryIndex] = React.useState(0);
  const { state, fetchBooks } = useContext(BooksContext);

  useEffect(() => {
    const unsub = navigation.addListener('focus', () => fetchBooks())
    return unsub
  }, [navigation])

  const CategoryList = ({ navigation }) => {
    return (
      <View style={styles.categoryContainer}>
        {categories.map((item, index) => (
          <TouchableOpacity key={index} activeOpacity={0.8} onPress={() => selectCategoryIndex(index)}>
            <Text style={[styles.categoryText,
            categoryIndex == index && styles.categoryTextSelected]}>
              {item}
            </Text>
          </TouchableOpacity>
        ))}
      </View>
    );
  };
  return (
    <SafeAreaView
      style={{
        flex: 1,
        paddingHorizontal: 20,
        backgroundColor: COLORS.white
      }}>
      <View style={styles.header}>
        <View>
          <Text style={{ fontSize: 25, fontWeight: 'bold' }}>Bine ai venit la</Text>
          <Text style={{ fontSize: 38, fontWeight: 'bold', color: COLORS.pink }}>Biblioteca Gloria</Text>
        </View>
        <Icon name="shopping-cart" size={28} />
        <TouchableOpacity onPress={() => { navigation.navigate('AccountScreen') }}>
          <FontAwesome name="user-circle-o" size={28} color="black" />
        </TouchableOpacity>
      </View>
      <View style={{ marginTop: 30, flexDirection: "row" }}>
        <View style={styles.searchContainer}>
          <Icon name="search" size={25} style={{ marginLeft: 20 }} />
          <TextInput placeholder="Search" style={styles.input} />
        </View>
        <View style={styles.sortBtn}>
          <Icon name="sort" size={30} color={COLORS.white} />
        </View>
      </View>
      <CategoryList />
      <FlatList
        columnWrapperStyle={{ justifyContent: 'space-between' }}
        showVerticalScrollIndicator={false}
        contentContainerStyle={{
          marginTop: 10,
          paddingBottom: 50
        }}
        numColumns={2}
        data={state}
        renderItem={({ item }) => <Card book={item} navigation={navigation} />}
      />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  header: {
    marginTop: 30,
    flexDirection: 'row',
    justifyContent: 'space-between'
  },
  searchContainer: {
    height: 50,
    backgroundColor: COLORS.light,
    borderRadius: 10,
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center'
  },
  input: {
    fontSize: 18,
    fontWeight: 'bold',
    color: COLORS.dark,
    flex: 1
  },
  sortBtn: {
    marginLeft: 10,
    height: 50,
    width: 50,
    backgroundColor: COLORS.pink,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 10
  },
  categoryContainer: {
    flexDirection: 'row',
    marginTop: 30,
    marginBottom: 20,
    justifyContent: 'space-between'
  },
  categoryText: {
    fontSize: 16,
    color: 'grey',
    fontWeight: 'bold'
  },
  categoryTextSelected: {
    color: COLORS.pink,
    paddingBottom: 5,
    borderBottomWidth: 2,
    borderColor: COLORS.pink
  }
});

export default withNavigation(BooksListScreen);
