import React from 'react'
import { Dimensions, StyleSheet, Text, View, Image } from "react-native";
import { Icon } from 'react-native-elements'
import { COLORS } from '../consts/colors';
import { TouchableOpacity } from 'react-native-gesture-handler';
const width = Dimensions.get('screen').width / 2 - 30;

const Card = ({ book, bookImage, navigation }) => {
    return (
        <TouchableOpacity onPress={() => navigation.navigate('BookDetails', { _id: book._id })}>
            <View style={styles.card}>
                <View style={{ alignItems: 'flex-end' }}>
                    <View style={{
                        width: 30,
                        height: 30,
                        borderRadius: 15,
                        alignItems: 'center',
                        justifyContent: 'center',
                        backgroundColor: book.like ? 'rgba(245,42,42,0.2' : 'rgba(0,0,0,0,2)',
                    }}>
                        <Icon name="favorite" size={18} color={book.like ? COLORS.red : COLORS.dark} />
                    </View>
                </View>
                <View style={{ height: 100, alignItems: 'center' }}>
                    <Image
                        style={{ flex: 1, resizeMode: 'contain' }}
                        source={bookImage}
                    />
                </View>
                <Text style={{ fontWeight: 'bold', fontSize: 17, marginTop: 10 }}>
                    {book.title}
                </Text>
                <View
                    style={{
                        flexDirection: 'row',
                        justifyContent: 'space-between',
                        marginTop: 5,
                    }}>
                    <Text style={{ fontSize: 19, fontWeight: 'bold' }}>
                        â‚¬{book.price}
                    </Text>
                    <View
                        style={{
                            height: 32,
                            width: 32,
                            backgroundColor: COLORS.pink,
                            borderRadius: 5,
                            justifyContent: 'center',
                            alignItems: 'center',
                        }}>
                        <Text
                            style={{ fontSize: 22, color: COLORS.white, fontWeight: 'bold' }}>
                            +
                        </Text>
                    </View>
                </View>
            </View>
        </TouchableOpacity>
    )
}

const styles = StyleSheet.create({
    card: {
        height: 225,
        backgroundColor: COLORS.light,
        width,
        marginHorizontal: 2,
        borderRadius: 10,
        marginBottom: 20,
        padding: 15
    }
});

export default Card;
